      "type": "string",
      "description": "Abreviação/identificação da turma. Exmplo: B, para segunda turma do 1º ano (de 1 Ano B)."
    },
    "grade_id": {
      "type": "number",
      "description": "ID do ano estudantíl."
    },
    "shift_id": {
      "type": "number",
      "description": "ID do turno."
    },
    "school_calendar_id": {
      "type": "number",
      "description": "ID do Ano letivo em que a turma existe."
    },
    "school_id": {
      "type": "number",
      "description": "ID da escola relacionada."
    },
    "evaluation_type": {
      "type": "string",
      "enum": [
        "grade_phase",
        "progress_sheet_phase"
      ],
      "description": "Definiçao do modo de avaliação da turma."
    },
    "progress_sheet_id": {
      "type": "number",
      "description": "ID da ficha descritiva utilizada para avaliar os alunos da turma."
    }
  }
}

