{
  "total": 25,
  "per_page": 15,
  "current_page": 1,
  "last_page": 2,
  "next_page_url": "http://localhost/api/<resource>?page=2",
  "prev_page_url": "null",
  "from": 1,
  "to": 15,
  "data": [
    {
      "id": 1,
      "lesson_plan_template_id": 1,
      "content": [
        {
          "name": "Objetivo",
          "type": "'text'",
          "required": true,
          "value": "'Resolver operações utilizando números decimais obedecendo regras.'"
        },
        {
          "name": "Conteúdo",
          "type": "'text'",
          "required": true,
          "value": "'Operações com números ordinais.'"
        },
        {
          "name": "Metodologia",
          "type": "'long",
          "required": true,
          "value": [
            "'Lorem ipsum dolor sit amet",
            "consectetur adipiscing elit.'"
          ]
        },
        {
